import "./App.css";
import { useState } from "react";
import AxiosRoutes from "./Routes/AxiosRoutes";

function App() {
  //Managers
  //Name
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  //EMP_ID
  const [empID, setEmpID] = useState(null);
  //Manager List
  const [managerList, setManagerList] = useState([]);
  //ManagerID
  const [managerID, setManagerID] = useState(null);
  //Show Managers
  const [managersOn, setManagersOn] = useState(false);

  //Employees
  //Name
  const [name, setName] = useState("");
  const [nameInput, setNameInput] = useState("");
  const [nameOn, setNameOn] = useState(false);
  const [newName, setNewName] = useState("");
  //Age
  const [age, setAge] = useState(0);
  const [ageInput, setAgeInput] = useState(0);
  const [ageOn, setAgeOn] = useState(false);
  const [newAge, setNewAge] = useState(0);
  //Country
  const [country, setCountry] = useState("");
  const [countryInput, setCountryInput] = useState("");
  const [countryOn, setCountryOn] = useState(false);
  const [newCountry, setNewCountry] = useState("");
  //Position
  const [position, setPosition] = useState("");
  const [positionInput, setPositionInput] = useState("");
  const [positionOn, setPositionOn] = useState(false);
  const [newPosition, setNewPosition] = useState("");
  //Wage
  const [wage, setWage] = useState(0);
  const [wageInput, setWageInput] = useState(0);
  const [wageOn, setWageOn] = useState(false);
  const [newWage, setNewWage] = useState(0);
  //Employee List
  const [employeeList, setEmployeeList] = useState([]);
  //Show Employees
  const [employeesOn, setEmployeesOn] = useState(false);
  //Delete Bttn
  const [hiddenBttn, setHiddenBttn] = useState(false);

  //Open the hidden delete button
  const openDelete = () => {
    if (hiddenBttn) {
      setHiddenBttn(false);
    } else if (!hiddenBttn) {
      setHiddenBttn(true);
    }
  };

  return (

    <div className="App">


      <h1>Employee Database</h1>
      {/* ////////   ADDING A NEW EMPLOYEE   /////// */}

              <div className="inputs">
                <label>Name:</label>
                <input
                  type="text"
                  onChange={(event) => {
                    setName(event.target.value);
                  }}
                />
                <label>Age:</label>
                <input
                  type="number"
                  onChange={(event) => {
                    setAge(event.target.value);
                  }}
                />
                <label>Country:</label>
                <input
                  type="text"
                  onChange={(event) => {
                    setCountry(event.target.value);
                  }}
                />
                <label>Position:</label>
                <input
                  type="text"
                  onChange={(event) => {
                    setPosition(event.target.value);
                  }}
                />
                <label>Wage per hr:</label>
                <input
                  type="number"
                  onChange={(event) => {
                    setWage(event.target.value);
                  }}
                />
                <label>Supervisor:</label>
                <input
                  type="number"
                  onChange={(event) => {
                    setManagerID(event.target.value);
                  }}
                />
                <button
                  className="infoBttn"
                  onClick={() => {
                    AxiosRoutes.addEmployee(
                      name,
                      age,
                      country,
                      position,
                      wage,
                      managerID,
                      setEmployeeList,
                      employeeList
                    );
                  }}
                >
                  Add Employee
                </button>
              </div>
      {/*    ////////  ADDING A NEW MANAGER  //////// */}
              <div className="inputs">
                <label>First Name:</label>
                <input
                  type="text"
                  onChange={(event) => {
                    setFirstName(event.target.value);
                  }}
                />
                <label>Last Name:</label>
                <input
                  type="text"
                  onChange={(event) => {
                    setLastName(event.target.value);
                  }}
                />
                <label>Employee ID:</label>
                <input
                  type="number"
                  onChange={(event) => {
                    setEmpID(event.target.value);
                  }}
                />

                <button
                  className="infoBttn"
                  onClick={() => {
                    AxiosRoutes.addManager(
                      firstName,
                      lastName,
                      empID,
                      setManagerList,
                      managerList
                    );
                  }}
                >
                  Add Manager
                </button>
              </div>
      {/* ////////    DISPLAY ALL EMPLOYEES BY FILTERS   ////////// */}
              <div className="employees">
                <div className="employeeHeader">
                  <div>Display All Employees By: </div>

                  <div className="employeeHeaderBttns">
                    {" "}
                    <div className="headerBttnGroup">
                      {" "}
                      <div>Name</div>
                      <button
                        onClick={() => {
                          AxiosRoutes.getEmployeesName(setEmployeeList);
                          setEmployeesOn(true);
                          setManagersOn(false);
                        }}
                      >
                        <span className="material-symbols-outlined">
                          keyboard_double_arrow_down
                        </span>
                      </button>
                      <button
                        onClick={() => {
                          AxiosRoutes.getEmployeesNameDesc(setEmployeeList);
                          setEmployeesOn(true);
                          setManagersOn(false);
                        }}
                      >
                        <span className="material-symbols-outlined">
                          keyboard_double_arrow_up
                        </span>
                      </button>
                    </div>
                    <div className="headerBttnGroup">
