import "./App.css";
import { useState } from "react";
import Axios from "axios";

function App() {
  const [name, setName] = useState("");
  const [nameInput, setNameInput] = useState("");
  const [nameOn, setNameOn] = useState(false);
  const [age, setAge] = useState(0);
  const [ageInput, setAgeInput] = useState(0);
  const [ageOn, setAgeOn] = useState(false);
  const [country, setCountry] = useState("");
  const [countryInput, setCountryInput] = useState("");
  const [countryOn, setCountryOn] = useState(false);
  const [position, setPosition] = useState("");
  const [positionInput, setPositionInput] = useState("");
  const [positionOn, setPositionOn] = useState(false);
  const [wage, setWage] = useState(0);
  const [wageInput, setWageInput] = useState(0);
  const [wageOn, setWageOn] = useState(false);
  const [employeeList, setEmployeeList] = useState([]);
  const [newWage, setNewWage] = useState(0);

  const addEmployee = () => {
    Axios.post("http://localhost:3001/create", {
      name: name,
      age: age,
      country: country,
      position: position,
      wage: wage,
    })
      .then(() => {
        setEmployeeList([
          ...employeeList,
          {
            name: name,
            age: age,
            country: country,
            position: position,
            wage: wage,
          },
        ]);
      })
      .catch((error) => {
        console.log(error.response);
      });
  };

  const getEmployees = () => {
    Axios.get("http://localhost:3001/employees").then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getEmployeesName = () => {
    Axios.get("http://localhost:3001/employeesName").then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getEmployeesAge = () => {
    Axios.get("http://localhost:3001/employeesAge").then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getEmployeesCountry = () => {
    Axios.get("http://localhost:3001/employeesCountry").then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getEmployeesPosition = () => {
    Axios.get("http://localhost:3001/employeesPosition").then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getEmployeesWage = () => {
    Axios.get("http://localhost:3001/employeesWage").then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getName = (name) => {
    Axios.get(`http://localhost:3001/name/${name}`).then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getAge = (age) => {
    Axios.get(`http://localhost:3001/age/${age}`).then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getCountry = (country) => {
    Axios.get(`http://localhost:3001/country/${country}`).then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getPosition = (position) => {
    Axios.get(`http://localhost:3001/position/${position}`).then((response) => {
      setEmployeeList(response.data);
    });
  };
  const getWage = (wage) => {
    Axios.get(`http://localhost:3001/wage/${wage}`).then((response) => {
      setEmployeeList(response.data);
    });
  };

  const updateEmployeeWage = (id) => {
    Axios.put("http://localhost:3001/update", { wage: newWage, id: id }).then(
      (response) => {
        setEmployeeList(
          employeeList.map((val) => {
            return val.id == id
              ? {
                  id: val.id,
                  name: val.name,
                  country: val.country,
                  age: val.age,
                  position: val.position,
                  wage: newWage,
                }
              : val;
          })
        );
      }
    );
  };

  const deleteEmployee = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setEmployeeList(
        employeeList.filter((val) => {
          return val.id != id;
        })
      );
    });
  };

  return (
    <div className="App">
      <h1>Employee Database</h1>
      <div className="inputs">
        <label>Name:</label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <label>Age:</label>
        <input
          type="number"
          onChange={(event) => {
            setAge(event.target.value);
          }}
        />
        <label>Country:</label>
        <input
          type="text"
          onChange={(event) => {
            setCountry(event.target.value);
          }}
        />
        <label>Position:</label>
        <input
          type="text"
          onChange={(event) => {
            setPosition(event.target.value);
          }}
        />
        <label>Wage per hr:</label>
        <input
