import "./App.css";
import { useState } from "react";
import AxiosRoutes from "./Routes/AxiosRoutes";

function App() {
  //////   Managers   //////
  //First Name
  const [firstName, setFirstName] = useState("");
  const [firstNameOn, setFirstNameOn] = useState(false);
  const [newFirstName, setNewFirstName] = useState("");
  const [firstNameInput, setFirstNameInput] = useState("");
  //Last Name
  const [lastName, setLastName] = useState("");
  const [lastNameOn, setLastNameOn] = useState(false);
  const [newLastName, setNewLastName] = useState("");
  const [lastNameInput, setLastNameInput] = useState("");
  //EMP_ID
  const [empID, setEmpID] = useState(0);
  const [IDOn, setIDOn] = useState(false);
  const [IDInput, setIDInput] = useState()
  //TITLE
  const [title, setTitle] = useState("");
  const [newTitle, setNewTitle] = useState("");
  const [titleOn, setTitleOn] = useState(false);
  //Manager List
  const [managerList, setManagerList] = useState([]);
  //Show Managers
  const [managersOn, setManagersOn] = useState(false);

  //////  Employees   ///////
  //Name
  const [name, setName] = useState("");
  const [nameInput, setNameInput] = useState("");
  const [nameOn, setNameOn] = useState(false);
  const [newName, setNewName] = useState("");
  //Age
  const [age, setAge] = useState(0);
  const [ageInput, setAgeInput] = useState(0);
  const [ageOn, setAgeOn] = useState(false);
  const [newAge, setNewAge] = useState(0);
  //Country
  const [country, setCountry] = useState("");
  const [countryInput, setCountryInput] = useState("");
  const [countryOn, setCountryOn] = useState(false);
  const [newCountry, setNewCountry] = useState("");
  //Position
  const [position, setPosition] = useState("");
  const [positionInput, setPositionInput] = useState("");
  const [positionOn, setPositionOn] = useState(false);
  const [newPosition, setNewPosition] = useState("");
  //Wage
  const [wage, setWage] = useState(0);
  const [wageInput, setWageInput] = useState(0);
  const [wageOn, setWageOn] = useState(false);
  const [newWage, setNewWage] = useState(0);
  //ManagerID
  const [managerID, setManagerID] = useState(0);
  //Employee List
  const [employeeList, setEmployeeList] = useState([]);
  //Show Employees
  const [employeesOn, setEmployeesOn] = useState(false);
  //Delete Bttn
  const [hiddenBttn, setHiddenBttn] = useState(false);

  //Open the hidden delete button
  const openDelete = () => {
    if (hiddenBttn) {
      setHiddenBttn(false);
    } else if (!hiddenBttn) {
      setHiddenBttn(true);
    }
  };

  return (
    <div className="App">
      <h1>Employee Database</h1>
      {/* ////////   ADDING A NEW EMPLOYEE   /////// */}

      <div className="inputs">
        <label>Name:</label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <label>Age:</label>
        <input
          type="number"
          onChange={(event) => {
            setAge(event.target.value);
          }}
        />
        <label>Country:</label>
        <input
          type="text"
          onChange={(event) => {
            setCountry(event.target.value);
          }}
        />
        <label>Position:</label>
        <input
          type="text"
          onChange={(event) => {
            setPosition(event.target.value);
          }}
        />
        <label>Wage per hr:</label>
        <input
          type="number"
          onChange={(event) => {
            setWage(event.target.value);
          }}
        />
        <label>Supervisor:</label>
        <input
          type="number"
          onChange={(event) => {
            setManagerID(event.target.value);
          }}
        />
        <button
          className="infoBttn"
          onClick={() => {
            AxiosRoutes.addEmployee(
              name,
              age,
              country,
              position,
              wage,
              managerID,
              setEmployeeList,
              employeeList
            );
          }}
        >
          Add Employee
        </button>
      </div>
      {/*    ////////  ADDING A NEW MANAGER  //////// */}
      <div className="inputs">
        <label>First Name:</label>
        <input
          type="text"
          onChange={(event) => {
            setFirstName(event.target.value);
          }}
        />
        <label>Last Name:</label>
        <input
          type="text"
          onChange={(event) => {
            setLastName(event.target.value);
          }}
        />
        <label>Title:</label>
        <input
          type="text"
          onChange={(event) => {
            setTitle(event.target.value);
          }}
        />
        <label>Employee ID:</label>
        <input
          type="number"
          onChange={(event) => {
            setEmpID(event.target.value);
          }}
        />

        <button
          className="infoBttn"
          onClick={() => {
            AxiosRoutes.addManager(
              firstName,
              lastName,
              empID,
              setManagerList,
              managerList,
              title
            );
          }}
        >
          Add Manager
        </button>
      </div>
      {/* ////////    DISPLAY ALL EMPLOYEES BY FILTERS   ////////// */}
      <div className="employees">
        <div className="employeeHeader">
          <div>Display All Employees By: </div>

          <div className="employeeHeaderBttns">
            {" "}
            <div className="headerBttnGroup">
              {" "}
              <div>Name</div>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesName(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesNameDesc(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              {" "}
              <div className="headerBttnGroupTitle">Age</div>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesAge(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesAgeDesc(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              {" "}
              <div className="headerBttnGroupTitle">Country</div>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesCountry(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesCountryDesc(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              <div className="headerBttnGroupTitle">Position</div>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesPosition(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesPositionDesc(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              <div className="headerBttnGroupTitle">Wage</div>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesWage(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button
                onClick={() => {
                  AxiosRoutes.getEmployeesWageDesc(setEmployeeList);
                  setEmployeesOn(true);
                  setManagersOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
          </div>
        </div>
      </div>
      {/* ////////    DISPLAY ALL MANAGERS BY FILTERS   ////////// */}
      <div className="employees">
        <div className="employeeHeader">
          <div>Display All Managers By: </div>

          <div className="employeeHeaderBttns">
            {" "}
            <div className="headerBttnGroup">
              {" "}
              <div>First Name</div>
              <button
                onClick={() => {
                  AxiosRoutes.getManagerFirstName(setManagerList);
                  setManagersOn(true);
                  setEmployeesOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button
                onClick={() => {
                  AxiosRoutes.getManagerFirstNameDesc(setManagerList);
                  setManagersOn(true);
                  setEmployeesOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              {" "}
              <div className="headerBttnGroupTitle">Last Name</div>
              <button
                onClick={() => {
                  AxiosRoutes.getManagerLastName(setManagerList);
                  setManagersOn(true);
                  setEmployeesOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button
                onClick={() => {
                  AxiosRoutes.getManagerLastNameDesc(setManagerList);
                  setManagersOn(true);
                  setEmployeesOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              {" "}
              <div className="headerBttnGroupTitle">Employee ID</div>
              <button
                onClick={() => {
                  AxiosRoutes.getManagersEmpID(setManagerList);
                  setManagersOn(true);
                  setEmployeesOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button
                onClick={() => {
                  AxiosRoutes.getManagersEmpIDDesc(setManagerList);
                  setManagersOn(true);
                  setEmployeesOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              {" "}
              <div className="headerBttnGroupTitle">Title</div>
              <button
                onClick={() => {
                  AxiosRoutes.getManagersTitle(setManagerList);
                  setManagersOn(true);
                  setEmployeesOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button
                onClick={() => {
                  AxiosRoutes.getManagersTitleDesc(setManagerList);
                  setManagersOn(true);
                  setEmployeesOn(false);
                }}
              >
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/*   /////////    SEARCH EMPLOYEES BY FILTER    ///////// */}
      <div className="searchBy">
        Search Employee by:
        <button
          onClick={() => {
            setNameOn(true);
            setAgeOn(false);
            setCountryOn(false);
            setPositionOn(false);
            setWageOn(false);
          }}
        >
          Name
        </button>
        <button
          onClick={() => {
            setNameOn(false);
            setAgeOn(true);
            setCountryOn(false);
            setPositionOn(false);
            setWageOn(false);
          }}
        >
          Age
        </button>
        <button
          onClick={() => {
            setNameOn(false);
            setAgeOn(false);
            setCountryOn(true);
            setPositionOn(false);
            setWageOn(false);
          }}
        >
          Country
        </button>
        <button
          onClick={() => {
            setNameOn(false);
            setAgeOn(false);
            setCountryOn(false);
            setPositionOn(true);
            setWageOn(false);
          }}
        >
          Position
        </button>
        <button
          onClick={() => {
            setNameOn(false);
            setAgeOn(false);
            setCountryOn(false);
            setPositionOn(false);
            setWageOn(true);
          }}
        >
          Wage
        </button>
      </div>

      {nameOn && (
        <div className="searchBar">
          {" "}
          Name:{" "}
          <input
            type="text"
            onChange={(event) => {
              setNameInput(event.target.value);
            }}
          />
          <button
            onClick={() => {
              AxiosRoutes.getName(nameInput, setEmployeeList);
            }}
          >
            Search
          </button>
        </div>
      )}
      {ageOn && (
        <div className="searchBar">
          {" "}
          Age:{" "}
          <input
            type="number"
            onChange={(event) => {
              setAgeInput(event.target.value);
            }}
          />
          <button
            onClick={() => {
              AxiosRoutes.getAge(ageInput, setEmployeeList);
            }}
          >
            Search
          </button>
        </div>
      )}
      {countryOn && (
        <div className="searchBar">
          {" "}
          Country:{" "}
          <input
            type="text"
            onChange={(event) => {
              setCountryInput(event.target.value);
            }}
          />
          <button
            onClick={() => {
              AxiosRoutes.getCountry(countryInput, setEmployeeList);
            }}
          >
            Search
          </button>
        </div>
      )}
      {positionOn && (
        <div className="searchBar">
          {" "}
          Position:{" "}
          <input
            type="text"
            onChange={(event) => {
              setPositionInput(event.target.value);
            }}
          />
          <button
            onClick={() => {
              AxiosRoutes.getPosition(positionInput, setEmployeeList);
            }}
          >
            Search
          </button>
        </div>
      )}
      {wageOn && (
        <div className="searchBar">
          {" "}
          Wage:{" "}
          <input
            type="number"
            onChange={(event) => {
              setWageInput(event.target.value);
            }}
          />
          <button
            onClick={() => {
              AxiosRoutes.getWage(wageInput, setEmployeeList);
            }}
          >
            Search
          </button>
        </div>
      )}
      {/*   /////////    SEARCH MANAGERS BY FILTER    ///////// */}
      <div className="searchBy">
        Search Managers by:
        <button
          onClick={() => {
            setFirstNameOn(true);
            setLastNameOn(false);
            setIDOn(false);
            setTitleOn(false);
          }}
        >
          First Name
        </button>
        <button
          onClick={() => {
            setFirstNameOn(false);
            setLastNameOn(true);
            setIDOn(false);
            setTitleOn(false);
          }}
        >
          Last Name
        </button>
        <button
          onClick={() => {
            setFirstNameOn(false);
            setLastNameOn(false);
            setIDOn(true);
            setTitleOn(false);
          }}
        >
          Employee ID
        </button>
        <button
          onClick={() => {
            setFirstNameOn(false);
            setLastNameOn(false);
            setIDOn(false);
            setTitleOn(true);
          }}
        >
          Title
        </button>
      </div>

      {firstNameOn && (
        <div className="searchBar">
          {" "}
          First Name:{" "}
          <input
            type="text"
            onChange={(event) => {
              setNameInput(event.target.value);
            }}
          />
          <button
            onClick={() => {
              AxiosRoutes.getName(nameInput, setEmployeeList);
            }}
          >
            Search
          </button>
        </div>
      )}
      {lastNameOn && (
        <div className="searchBar">
          {" "}
          Last Name:{" "}
          <input
            type="number"
            onChange={(event) => {
              setAgeInput(event.target.value);
            }}
          />
          <button
            onClick={() => {
              AxiosRoutes.getAge(ageInput, setEmployeeList);
            }}
          >
            Search
          </button>
        </div>
      )}
      {IDOn && (
        <div className="searchBar">
          {" "}
          Employee ID:{" "}
          <input
            type="text"
            onChange={(event) => {
              setCountryInput(event.target.value);
            }}
          />
          <button
            onClick={() => {
              AxiosRoutes.getCountry(countryInput, setEmployeeList);
            }}
          >
            Search
          </button>
        </div>
      )}
      {titleOn && (
        <div className="searchBar">
          {" "}
          Title:{" "}
          <input
            type="text"
            onChange={(event) => {
              setPositionInput(event.target.value);
            }}
          />
          <button
            onClick={() => {
              AxiosRoutes.getPosition(positionInput, setEmployeeList);
            }}
          >
            Search
          </button>
        </div>
      )}

      {/* ///////////    MAPPING OUT EMPLOYEES TO THE PAGE   /////////// */}
      {employeesOn && (
        <div>
          {employeeList.map((val, index) => {
            return (
              <div className="employee">
                <div className="itemHolder">
                  <div className="item"> Employee ID:</div>
                  <div className="item"> {val.id}</div>
                </div>
                <div className="itemHolder">
                  <div className="item"> Name:</div>
                  <div className="item">
                    {" "}
                    {val.name}
                    <div className="newWageHolder">
                      <input
