import "./App.css";
import { useState } from "react";
import Axios from "axios";

function App() {
  const [name, setName] = useState("");
  const [nameInput, setNameInput] = useState("");
  const [nameOn, setNameOn] = useState(false);
  const [newName, setNewName] = useState(0);
  const [age, setAge] = useState(0);
  const [ageInput, setAgeInput] = useState(0);
  const [ageOn, setAgeOn] = useState(false);
  const [country, setCountry] = useState("");
  const [countryInput, setCountryInput] = useState("");
  const [countryOn, setCountryOn] = useState(false);
  const [position, setPosition] = useState("");
  const [positionInput, setPositionInput] = useState("");
  const [positionOn, setPositionOn] = useState(false);
  const [wage, setWage] = useState(0);
  const [wageInput, setWageInput] = useState(0);
  const [wageOn, setWageOn] = useState(false);
  const [employeeList, setEmployeeList] = useState([]);
  const [newWage, setNewWage] = useState(0);

  //Add a new employee
  const addEmployee = () => {
    Axios.post("http://localhost:3001/create", {
      name: name,
      age: age,
      country: country,
      position: position,
      wage: wage,
    })
      .then(() => {
        setEmployeeList([
          ...employeeList,
          {
            name: name,
            age: age,
            country: country,
            position: position,
            wage: wage,
          },
        ]);
      })
      .catch((error) => {
        console.log(error.response);
      });
  };
  //Get all employees unordered
  const getEmployees = () => {
    Axios.get("http://localhost:3001/employees").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get all employees ordered by name ASC
  const getEmployeesName = () => {
    Axios.get("http://localhost:3001/employeesName").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get all employees ordered by name DESC
  const getEmployeesNameDesc = () => {
    Axios.get("http://localhost:3001/employeesNameDesc").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get all employees ordered by age ASC
  const getEmployeesAge = () => {
    Axios.get("http://localhost:3001/employeesAge").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get all employees ordered by age DESC
  const getEmployeesAgeDesc = () => {
    Axios.get("http://localhost:3001/employeesAgeDesc").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get all employees ordered by country ASC
  const getEmployeesCountry = () => {
    Axios.get("http://localhost:3001/employeesCountry").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get all employees ordered by country DESC
  const getEmployeesCountryDesc = () => {
    Axios.get("http://localhost:3001/employeesCountryDesc").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get all employees ordered by position ASC
  const getEmployeesPosition = () => {
    Axios.get("http://localhost:3001/employeesPosition").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get all employees ordered by position DESC
  const getEmployeesPositionDesc = () => {
    Axios.get("http://localhost:3001/employeesPositionDesc").then(
      (response) => {
        setEmployeeList(response.data);
      }
    );
  };
  //Get all employees ordered by wage ASC
  const getEmployeesWage = () => {
    Axios.get("http://localhost:3001/employeesWage").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get all employees ordered by wage DESC
  const getEmployeesWageDesc = () => {
    Axios.get("http://localhost:3001/employeesWageDesc").then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get employees by searching a name
  const getName = (name) => {
    Axios.get(`http://localhost:3001/name/${name}`).then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get employees by searching an age
  const getAge = (age) => {
    Axios.get(`http://localhost:3001/age/${age}`).then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get employees by searching a country
  const getCountry = (country) => {
    Axios.get(`http://localhost:3001/country/${country}`).then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get employees by searching a position
  const getPosition = (position) => {
    Axios.get(`http://localhost:3001/position/${position}`).then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Get employees by searching a wage
  const getWage = (wage) => {
    Axios.get(`http://localhost:3001/wage/${wage}`).then((response) => {
      setEmployeeList(response.data);
    });
  };
  //Update an employees name
  const updateEmployeeName = (id) => {
    Axios.put("http://localhost:3001/updateName", { name: newName, id: id }).then(
      (response) => {
        setEmployeeList(
          employeeList.map((val) => {
            return val.id == id
              ? {
                  id: val.id,
                  name: newName,
                  country: val.country,
                  age: val.age,
                  position: val.position,
                  wage: val.wage,
                }
              : val;
          })
        );
      }
    );
  };
  //Update an employees age
  const updateEmployeeAge = (id) => {
    Axios.put("http://localhost:3001/updateAge", { age: newAge, id: id }).then(
      (response) => {
        setEmployeeList(
          employeeList.map((val) => {
            return val.id == id
              ? {
                  id: val.id,
                  name: val.name,
                  country: val.country,
                  age: newAge,
                  position: val.position,
                  wage: val.wage,
                }
              : val;
          })
        );
      }
    );
  };
  //Update an employees country
  const updateEmployeeCountry = (id) => {
    Axios.put("http://localhost:3001/update", { country: newCountry, id: id }).then(
      (response) => {
        setEmployeeList(
          employeeList.map((val) => {
            return val.id == id
              ? {
                  id: val.id,
                  name: val.name,
                  country: newCountry,
                  age: val.age,
                  position: val.position,
                  wage: val.wage,
                }
              : val;
          })
        );
      }
    );
  };
  //Update an employees position
  const updateEmployeePosition = (id) => {
    Axios.put("http://localhost:3001/updatePosition", { position: newPosition, id: id }).then(
      (response) => {
        setEmployeeList(
          employeeList.map((val) => {
            return val.id == id
              ? {
                  id: val.id,
                  name: val.name,
                  country: val.country,
                  age: val.age,
                  position: newPosition,
                  wage: val.wage,
                }
              : val;
          })
        );
      }
    );
  };
  //Update an employees wage
  const updateEmployeeWage = (id) => {
    Axios.put("http://localhost:3001/updateWage", { wage: newWage, id: id }).then(
      (response) => {
        setEmployeeList(
          employeeList.map((val) => {
            return val.id == id
              ? {
                  id: val.id,
                  name: val.name,
                  country: val.country,
                  age: val.age,
                  position: val.position,
                  wage: newWage,
                }
              : val;
          })
        );
      }
    );
  };
  //Delete an employee
  const deleteEmployee = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setEmployeeList(
        employeeList.filter((val) => {
          return val.id != id;
        })
      );
    });
  };

  return (
    <div className="App">
      <h1>Employee Database</h1>
      <div className="inputs">
        <label>Name:</label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <label>Age:</label>
        <input
          type="number"
          onChange={(event) => {
            setAge(event.target.value);
          }}
        />
        <label>Country:</label>
        <input
          type="text"
          onChange={(event) => {
            setCountry(event.target.value);
          }}
        />
        <label>Position:</label>
        <input
          type="text"
          onChange={(event) => {
            setPosition(event.target.value);
          }}
        />
        <label>Wage per hr:</label>
        <input
          type="number"
          onChange={(event) => {
            setWage(event.target.value);
          }}
        />
        <button className="infoBttn" onClick={addEmployee}>
          Add Employee
        </button>
      </div>
      <div className="employees">
        <div className="employeeHeader">
          <div>Display All Employees By: </div>

          <div className="employeeHeaderBttns">
            {" "}
            <div className="headerBttnGroup">
              {" "}
              <div>Name</div>
              <button onClick={getEmployeesName}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={getEmployeesNameDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              {" "}
              <div className="headerBttnGroupTitle">Age</div>
              <button onClick={getEmployeesAge}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={getEmployeesAgeDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              {" "}
              <div className="headerBttnGroupTitle">Country</div>
              <button onClick={getEmployeesCountry}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={getEmployeesCountryDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              <div className="headerBttnGroupTitle">Position</div>
              <button onClick={getEmployeesPosition}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={getEmployeesPositionDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              <div className="headerBttnGroupTitle">Wage</div>
              <button onClick={getEmployeesWage}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={getEmployeesWageDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
          </div>
        </div>

        <div>
          Search by:
          <button
            onClick={() => {
              setNameOn(true);
              setAgeOn(false);
              setCountryOn(false);
              setPositionOn(false);
              setWageOn(false);
            }}
          >
            Name
          </button>
          <button
            onClick={() => {
              setNameOn(false);
              setAgeOn(true);
              setCountryOn(false);
              setPositionOn(false);
              setWageOn(false);
            }}
          >
            Age
          </button>
          <button
            onClick={() => {
              setNameOn(false);
              setAgeOn(false);
              setCountryOn(true);
              setPositionOn(false);
              setWageOn(false);
            }}
          >
            Country
          </button>
          <button
            onClick={() => {
              setNameOn(false);
              setAgeOn(false);
              setCountryOn(false);
              setPositionOn(true);
              setWageOn(false);
            }}
          >
            Position
          </button>
          <button
            onClick={() => {
              setNameOn(false);
              setAgeOn(false);
              setCountryOn(false);
              setPositionOn(false);
              setWageOn(true);
            }}
          >
            Wage
          </button>
        </div>

        {nameOn && (
          <div>
            {" "}
            Name:{" "}
            <input
              type="text"
              onChange={(event) => {
                setNameInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                getName(nameInput);
              }}
            >
              Search
            </button>
          </div>
        )}
        {ageOn && (
          <div>
            {" "}
            Age:{" "}
            <input
              type="number"
              onChange={(event) => {
                setAgeInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                getAge(ageInput);
              }}
            >
              Search
            </button>
          </div>
        )}
        {countryOn && (
          <div>
            {" "}
            Country:{" "}
            <input
              type="text"
              onChange={(event) => {
                setCountryInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                getCountry(countryInput);
              }}
            >
              Search
            </button>
          </div>
        )}
        {positionOn && (
          <div>
            {" "}
            Position:{" "}
            <input
              type="text"
              onChange={(event) => {
                setPositionInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                getPosition(positionInput);
              }}
            >
              Search
            </button>
          </div>
        )}
        {wageOn && (
          <div>
            {" "}
            Wage:{" "}
            <input
              type="number"
              onChange={(event) => {
                setWageInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                getWage(wageInput);
              }}
            >
              Search
            </button>
          </div>
        )}
        {employeeList.map((val, key) => {
          return (
            <div className="employee">
              <div className="itemHolder">
                <div className="item"> Name:</div>
                <div className="item"> {val.name}</div>
              </div>
              <div className="itemHolder">
                <div className="item">Age:</div>
                <div className="item">{val.age}</div>
              </div>

              <div className="itemHolder">
                <div className="item">Country:</div>
                <div className="item">{val.country}</div>{" "}
              </div>
              <div className="itemHolder">
                {" "}
                <div className="item">Position:</div>{" "}
                <div className="item">{val.position}</div>
              </div>
              <div className="itemHolder">
                {" "}
                <div className="item"> Wage:</div>{" "}
                <div className="item"> ${val.wage}hr</div>
              </div>
              <div className="newWageHolder">
                <input
                  className="newWageInput"
                  type="text"
                  placeholder="new wage"
                  onChange={(event) => {
                    setNewWage(event.target.value);
                  }}
                />
                <button
                  onClick={() => {
                    updateEmployeeWage(val.id);
                  }}
                >
                  {" "}
                  Update
                </button>
              </div>
              <div className="deleteBttn">
                {" "}
                <button
                  onClick={() => {
                    deleteEmployee(val.id);
                  }}
                >
                  Delete Employee
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
