import "./App.css";
import { useState } from "react";
import Axios from "axios";
import AxiosRoutes from "./Routes/AxiosRoutes";

function App() {
  //Managers
  //Name
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  //EMP_ID
  const [empID, setEmpID] = useState(null);
  //Manager List
  const [managerList, setManagerList] = useState([]);

  //Employees
  //Name
  const [name, setName] = useState("");
  const [nameInput, setNameInput] = useState("");
  const [nameOn, setNameOn] = useState(false);
  const [newName, setNewName] = useState("");
  //Age
  const [age, setAge] = useState(0);
  const [ageInput, setAgeInput] = useState(0);
  const [ageOn, setAgeOn] = useState(false);
  const [newAge, setNewAge] = useState(0);
  //Country
  const [country, setCountry] = useState("");
  const [countryInput, setCountryInput] = useState("");
  const [countryOn, setCountryOn] = useState(false);
  const [newCountry, setNewCountry] = useState("");
  //Position
  const [position, setPosition] = useState("");
  const [positionInput, setPositionInput] = useState("");
  const [positionOn, setPositionOn] = useState(false);
  const [newPosition, setNewPosition] = useState("");
  //Wage
  const [wage, setWage] = useState(0);
  const [wageInput, setWageInput] = useState(0);
  const [wageOn, setWageOn] = useState(false);
  const [newWage, setNewWage] = useState(0);
  //ManagerID
  const [managerID, setManagerID] = useState(null);
  //Employee List
  const [employeeList, setEmployeeList] = useState([]);
  //Delete Bttn
  const [hiddenBttn, setHiddenBttn] = useState(false);

  //Open the hidden delete button
  const openDelete = () => {
    if (hiddenBttn) {
      setHiddenBttn(false);
    } else if (!hiddenBttn) {
      setHiddenBttn(true);
    }
  };



  return (
    <div className="App">
      <h1>Employee Database</h1>
      <div className="inputs">
        <label>Name:</label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <label>Age:</label>
        <input
          type="number"
          onChange={(event) => {
            setAge(event.target.value);
          }}
        />
        <label>Country:</label>
        <input
          type="text"
          onChange={(event) => {
            setCountry(event.target.value);
          }}
        />
        <label>Position:</label>
        <input
          type="text"
          onChange={(event) => {
            setPosition(event.target.value);
          }}
        />
        <label>Wage per hr:</label>
        <input
          type="number"
          onChange={(event) => {
            setWage(event.target.value);
          }}
        />
        <label>Manager ID:</label>
        <input
          type="number"
          onChange={(event) => {
            setManagerID(event.target.value);
          }}
        />
        <button className="infoBttn" onClick={addEmployee}>
          Add Employee
        </button>
      </div>

      <div className="inputs">
        <label>First Name:</label>
        <input
          type="text"
          onChange={(event) => {
            setFirstName(event.target.value);
          }}
        />
        <label>Last Name:</label>
        <input
          type="text"
          onChange={(event) => {
            setLastName(event.target.value);
          }}
        />
        <label>Employee ID:</label>
        <input
          type="number"
          onChange={(event) => {
            setEmpID(event.target.value);
          }}
        />

        <button className="infoBttn" onClick={AxiosRoutes.addManager}>
          Add Manager
        </button>
      </div>

      <div className="employees">
        <div className="employeeHeader">
          <div>Display All Employees By: </div>

          <div className="employeeHeaderBttns">
            {" "}
            <div className="headerBttnGroup">
              {" "}
              <div>Name</div>
              <button onClick={AxiosRoutes.getEmployeesName}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={AxiosRoutes.getEmployeesNameDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              {" "}
              <div className="headerBttnGroupTitle">Age</div>
              <button onClick={AxiosRoutes.getEmployeesAge}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={AxiosRoutes.getEmployeesAgeDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              {" "}
              <div className="headerBttnGroupTitle">Country</div>
              <button onClick={AxiosRoutes.getEmployeesCountry}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={AxiosRoutes.getEmployeesCountryDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              <div className="headerBttnGroupTitle">Position</div>
              <button onClick={AxiosRoutes.getEmployeesPosition}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={AxiosRoutes.getEmployeesPositionDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
            <div className="headerBttnGroup">
              <div className="headerBttnGroupTitle">Wage</div>
              <button onClick={AxiosRoutes.getEmployeesWage}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_down
                </span>
              </button>
              <button onClick={AxiosRoutes.getEmployeesWageDesc}>
                <span className="material-symbols-outlined">
                  keyboard_double_arrow_up
                </span>
              </button>
            </div>
          </div>
        </div>

        <div className="searchBy">
          Search by:
          <button
            onClick={() => {
              setNameOn(true);
              setAgeOn(false);
              setCountryOn(false);
              setPositionOn(false);
              setWageOn(false);
            }}
          >
            Name
          </button>
          <button
            onClick={() => {
              setNameOn(false);
              setAgeOn(true);
              setCountryOn(false);
              setPositionOn(false);
              setWageOn(false);
            }}
          >
            Age
          </button>
          <button
            onClick={() => {
              setNameOn(false);
              setAgeOn(false);
              setCountryOn(true);
              setPositionOn(false);
              setWageOn(false);
            }}
          >
            Country
          </button>
          <button
            onClick={() => {
              setNameOn(false);
              setAgeOn(false);
              setCountryOn(false);
              setPositionOn(true);
              setWageOn(false);
            }}
          >
            Position
          </button>
          <button
            onClick={() => {
              setNameOn(false);
              setAgeOn(false);
              setCountryOn(false);
              setPositionOn(false);
              setWageOn(true);
            }}
          >
            Wage
          </button>
        </div>

        {nameOn && (
          <div className="searchBar">
            {" "}
            Name:{" "}
            <input
              type="text"
              onChange={(event) => {
                setNameInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                getName(nameInput);
              }}
            >
              Search
            </button>
          </div>
        )}
        {ageOn && (
          <div className="searchBar">
            {" "}
            Age:{" "}
            <input
              type="number"
              onChange={(event) => {
                setAgeInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                AxiosRoutes.getAge(ageInput);
              }}
            >
              Search
            </button>
          </div>
        )}
        {countryOn && (
          <div className="searchBar">
            {" "}
            Country:{" "}
            <input
              type="text"
              onChange={(event) => {
                setCountryInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                AxiosRoutes.getCountry(countryInput);
              }}
            >
              Search
            </button>
          </div>
        )}
        {positionOn && (
          <div className="searchBar">
            {" "}
            Position:{" "}
            <input
              type="text"
              onChange={(event) => {
                setPositionInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                AxiosRoutes.getPosition(positionInput);
              }}
            >
              Search
            </button>
          </div>
        )}
        {wageOn && (
          <div className="searchBar">
            {" "}
            Wage:{" "}
            <input
              type="number"
              onChange={(event) => {
                setWageInput(event.target.value);
              }}
            />
            <button
              onClick={() => {
                AxiosRoutes.getWage(wageInput);
              }}
            >
              Search
            </button>
          </div>
        )}

        {employeeList.map((val, index) => {
          return (
            <div className="employee">
              <div className="itemHolder">
                <div className="item"> Employee ID:</div>
                <div className="item"> {val.id}</div>
              </div>
              <div className="itemHolder">
                <div className="item"> Name:</div>
                <div className="item">
                  {" "}
                  {val.name}
                  <div className="newWageHolder">
                    <input
                      className="newInput"
                      type="text"
                      placeholder="new name"
                      onChange={(event) => {
                        setNewName(event.target.value);
                      }}
                    />
                    <button
                      onClick={() => {
                        AxiosRoutes.updateEmployeeName(val.id);
                      }}
                    >
                      {" "}
                      Update
                    </button>
                  </div>
                </div>
              </div>
              <div className="itemHolder">
                <div className="item">Age:</div>
                <div className="item">
                  {val.age}
                  <div className="newWageHolder">
                    <input
                      className="newInput"
                      type="text"
                      placeholder="new age"
                      onChange={(event) => {
                        setNewAge(event.target.value);
                      }}
                    />
                    <button
                      onClick={() => {
                        AxiosRoutes.updateEmployeeAge(val.id);
                      }}
                    >
                      {" "}
                      Update
                    </button>
                  </div>
                </div>
              </div>

              <div className="itemHolder">
                <div className="item">Country:</div>
                <div className="item">{val.country}</div>
                <div className="newWageHolder">
                  <input
                    className="newInput"
                    type="text"
                    placeholder="new country"
                    onChange={(event) => {
                      setNewCountry(event.target.value);
                    }}
                  />
                  <button
                    onClick={() => {
                      AxiosRoutes.updateEmployeeCountry(val.id);
                    }}
                  >
                    {" "}
                    Update
                  </button>
                </div>{" "}
              </div>
              <div className="itemHolder">
                {" "}
                <div className="item">Position:</div>{" "}
                <div className="item">{val.position}</div>
                <div className="newWageHolder">
                  <input
                    className="newInput"
                    type="text"
                    placeholder="new position"
                    onChange={(event) => {
                      setNewPosition(event.target.value);
                    }}
                  />
                  <button
                    onClick={() => {
                      AxiosRoutes.updateEmployeePosition(val.id);
                    }}
                  >
                    {" "}
                    Update
                  </button>
                </div>
              </div>
              <div className="itemHolder">
                {" "}
                <div className="item"> Wage:</div>{" "}
                <div className="item"> ${val.wage}hr</div>
                <div className="newWageHolder">
                  <input
                    className="newInput"
                    type="text"
                    placeholder="new wage"
                    onChange={(event) => {
                      setNewWage(event.target.value);
                    }}
                  />
                  <button
                    onClick={() => {
                      AxiosRoutes.updateEmployeeWage(val.id);
                    }}
                  >
                    {" "}
                    Update
                  </button>
                </div>
              </div>

              <div className="deleteBttn">
                {" "}
                <button
                  key={val.id}
                  onClick={() => {
                    openDelete();
                  }}
                >
                  Delete Employee
                </button>
                {hiddenBttn && (
                  <div>
                    Are you sure?
                    <button
                      onClick={() => {
                        deleteEmployee(val.id);
                      }}
                    >
                      yes
                    </button>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
